changeSet.sql:
ALTER TABLE projects ADD COLUMN owner_id bigint NOT NULL;

ALTER TABLE categories ADD COLUMN owner_id bigint NOT NULL;

ALTER TABLE money_transactions ADD COLUMN owner_id bigint NOT NULL;

ALTER TABLE categories ADD CONSTRAINT categories_name_ui UNIQUE (owner_id,name);

ALTER TABLE projects ADD CONSTRAINT projects_owner_id_fk FOREIGN KEY (owner_id)
REFERENCES users (id) MATCH SIMPLE
ON DELETE NO ACTION ON UPDATE NO ACTION;

ALTER TABLE categories ADD CONSTRAINT categories_owner_id_fk FOREIGN KEY (owner_id)
REFERENCES users (id) MATCH SIMPLE
ON DELETE RESTRICT ON UPDATE NO ACTION;

ALTER TABLE money_transactions ADD CONSTRAINT money_transactions_owner_id_fk FOREIGN KEY (owner_id)
REFERENCES users (id) MATCH SIMPLE
ON DELETE NO ACTION ON UPDATE NO ACTION;
